import React, {Component} from 'react';
import {Modal, Icon, Input, InputNumber, Form} from 'antd';


const FormItem = Form.Item;

const EditForm = Form.create()(
    (props) => {
        const {visible, playedTime, onCancel, onCreate, add, remove, form} = props;

        const {getFieldDecorator, getFieldValue} = form;
        const formItemLayout = {
            labelCol: {
                xs: {span: 24},
                sm: {span: 4},
            },
            wrapperCol: {
                xs: {span: 24},
                sm: {span: 20},
            },
        };
        const formItemLayoutWithOutLabel = {
            wrapperCol: {
                xs: {span: 24, offset: 0},
                sm: {span: 20, offset: 4},
            },
        };
        return (
            <Form {...formItemLayout}>
                <Form.Item>
                    {getFieldDecorator(`editedText`, {
                        validateTrigger: ['onChange', 'onBlur'],
                        rules: [],
                    })(
                        <Input size="large" defaultValue={this.props.text} style={{width: '60%', marginRight: 8}}/>
                    )}
                </Form.Item>
            </Form>
        )
    })

class EditModal extends Component {
    state = {visible: false};

    formItemLayout = {
        labelCol: {
            xs: {span: 24},
            sm: {span: 5},
        },
        wrapperCol: {
            xs: {span: 24},
            sm: {span: 12},
        },
    };

    showModal = () => {
        this.setState({visible: true});

    };

    handleOk = e => {
        console.log(e);
        this.props.handleSubmit(e);
        this.setState({visible: false});

    };

    handleCancel = e => {
        console.log(e);
        this.setState({visible: false});
    };

    render() {
        console.log(this.props.text);
        return (
            <a>
                <Icon type="edit" onClick={this.showModal} theme="twoTone"/>
                <Modal
                    title="Basic Modal"
                    visible={this.state.visible}
                    onOk={this.handleOk}
                    onCancel={this.handleCancel}>
                    {/*<InputNumber min={0} max={10} defaultValue={this.props.time} step={0.01}/>*/}
                    {/*<Input size="large" defaultValue={this.props.text}/>*/}
                    <EditForm
                        text={this.props.text}/>
                </Modal>
            </a>
        );
    }
}

export default EditModal;
