import React, {Component} from 'react';
import {Modal, Icon, Input, InputNumber} from 'antd';

class EditModal extends Component {
    state = {visible: false};

    formItemLayout = {
        labelCol: {
            xs: { span: 24 },
            sm: { span: 5 },
        },
        wrapperCol: {
            xs: { span: 24 },
            sm: { span: 12 },
        },
    };

    showModal = () => {
        this.setState({visible: true});

    };

    handleOk = e => {
        console.log(e);
        this.props.handleSubmit(e);
        this.setState({visible: false});

    };

    handleCancel = e => {
        console.log(e);
        this.setState({visible: false});
    };

    render() {
        return (
            <a>
                <Icon type="edit" onClick={this.showModal} theme="twoTone"/>
                <Modal
                    title="Basic Modal"
                    visible={this.state.visible}
                    onOk={this.handleOk}
                    onCancel={this.handleCancel}>
                    <InputNumber min={0} max={10} defaultValue={this.props.time} step={0.01}/>
                    <Input size="large" defaultValue={this.props.text}/>
                </Modal>
            </a>
        );
    }
}

export default EditModal;
