import React, {Component} from 'react';
import axios from 'axios'
import {parse, toSeconds} from 'iso8601-duration';
import KeyboardEventHandler from 'react-keyboard-event-handler';

import {Icon} from "antd";
import ReactPlayer from 'react-player'


class VideoDetail extends Component {
    state = {
        player: null,
        videoDuration: null,
        currentTime: 0
    };

    constructor(props) {
        super(props);
        // create a ref to store the textInput DOM element
        this.ytPlayer = React.createRef();
        this.setWrapperRef = this.setWrapperRef.bind(this);
        this.handleEnter = this.handleEnter.bind(this);
    }


    componentDidUpdate(prevProps) {
        if (this.props.video && ( prevProps.video !== this.props.video)) {

            const ytUrl = `https://www.googleapis.com/youtube/v3/videos?id=${ this.props.video.id.videoId }&part=contentDetails&key=${ this.props.ytKey }`;
            axios.get(ytUrl).then(res => this.setState({
                videoDuration: toSeconds(parse(res.data.items[0].contentDetails.duration))
            }))
        }
        console.log(this.state.videoDuration)
    }

    componentDidMount() {
        document.addEventListener('keypress', this.handleEnter());
    }

    componentWillUnmount() {
        document.removeEventListener('keypress', this.handleEnter());
    }

    setWrapperRef(node) {
        this.wrapperRef = node;
    }

    handleEnter(event) {
        console.log(event);
        if (this.wrapperRef && !this.wrapperRef.contains(event.target)) {
            alert('You pressed enter');
        }
    }


    inputQuestion(e) {
        console.log(e.key);
        console.log("inputQuestion");
    };

    render() {
        const video = this.props.video;

        if (!video.id) {
            return (
                <div style={{
                    "width": "67.5%",
                    "background": "#999999",
                    "color": "#fff",
                    "height": "85vh",
                    "postion": "relative"
                }}>
                    <h1 style={{"top": "50%", "left": "28%", "position": "absolute"}}><Icon type={"youtube"}/></h1>
                </div>
            )
        }

        const videoId = video.id.videoId;
        const url = `https://www.youtube.com/embed/${ videoId }`;

        return (
            <div>
                <div ref={this.setWrapperRef}>{this.props.children}</div>
                <KeyboardEventHandler
                    handleKeys={['a', 'b', 'c', 'enter']}
                    onKeyEvent={(key, e) => console.log(`do something upon keydown event of ${key}`)}>
                    <div className={"embed-responsive embed-responsive-16by9"}>
                        <ReactPlayer controls url={url} ref={this.ytPlayer}/>
                    </div>
                </KeyboardEventHandler>
                <div>
                    <h2>
                        {this.props.video.snippet.title}
                    </h2>
                    <div>
                        {this.props.video.snippet.description}
                    </div>
                </div>
            </div>
        )
    }

    toggleMenu = (event) => {
        event.preventDefault();

        this.setState({ showMenu: !this.state.showMenu });
    };
}

export default VideoDetail;
