import React, {Component} from 'react';
import Slider from 'react-input-slider';

import {Icon} from "antd";
import ReactPlayer from 'react-player'


class VideoDetail extends Component {
    state = {
        video: null,
        player: null,
        x:20
    };



    componentDidUpdate(prevProps) {
        if (this.props.video && ( prevProps.video !== this.props.video)) {
            this.setState({video: this.props.video});
        }
    }

    paused(e) {
        console.log(e);
    }

    onSeek(e) {
        console.log("seeked");
    }

    onProgress(e) {
        console.log("progress");
        console.log(e);
    }

    onDuration(e) {
        console.log("duration");
        console.log(e);
    }
    doubleClicked(e) {
        console.log("double");
        console.log(e);
    }


    render() {
        const video = this.state.video;

        if (!video) {
            return (
                <div style={{
                    "width": "67.5%",
                    "background": "#999999",
                    "color": "#fff",
                    "height": "85vh",
                    "postion": "relative"
                }}>
                    <h1 style={{"top": "50%", "left": "28%", "position": "absolute"}}><Icon type={"youtube"}/></h1>
                </div>
            )
        }

        console.log(this.props);
        const videoId = video.id ? this.props.video.id.videoId: null;
        const url = `https://www.youtube.com/embed/${ videoId }`;
        const length = this.props.video ? this.props.video.length: 0;

        return (
            <div>
                <div className={"embed-responsive embed-responsive-16by9"}>

                    <ReactPlayer url='https://www.youtube.com/watch?v=OSSpVLpuVWA' controls
                                 onPause={this.paused} onSeek={this.onSeek} onProgress={this.onProgress}
                                 onDuration={this.onDuration}/>
                    <Slider
                        axis="x"
                        xstep={0.01}
                        xmin={0}
                        xmax={length}
                        x={this.state.x}
                        onDoubleClick={this.doubleClicked}
                        onChange={({ x }) => this.setState({ x: parseFloat(x.toFixed(2)) })}
                    />

                    {/*<iframe title={video.snippet.title} className={"embed-responsive-item"} src={url} allowFullScreen/>*/}
                </div>
                <div>
                <h2>
                {this.props.video.snippet.title}
                </h2>
                <div>
                {this.props.video.snippet.description}
                </div>
                </div>
            </div>
        )
    }
}

export default VideoDetail;
