import React, {Component} from 'react';
import {Modal, Icon, Input, InputNumber, Form} from 'antd';


const EditForm = Form.create()(
    (props) => {
        const {text, form} = props;

        const {getFieldDecorator, getFieldValue} = form;
        const formItemLayout = {
            labelCol: {
                xs: {span: 24},
                sm: {span: 4},
            },
            wrapperCol: {
                xs: {span: 24},
                sm: {span: 20},
            },
        };
        const formItemLayoutWithOutLabel = {
            wrapperCol: {
                xs: {span: 24, offset: 0},
                sm: {span: 20, offset: 4},
            },
        };
        return (
            <Form {...formItemLayout}>
                <Form.Item>
                    {getFieldDecorator(`editedText`, {
                        validateTrigger: ['onChange', 'onBlur'],
                        rules: [],
                    })(
                        <Input size="large" placeholder={text} style={{width: '60%', marginRight: 8}}/>
                    )}
                </Form.Item>
            </Form>
        )
    })

class EditModal extends Component {
    state = {visible: false};

    formItemLayout = {
        labelCol: {
            xs: {span: 24},
            sm: {span: 5},
        },
        wrapperCol: {
            xs: {span: 24},
            sm: {span: 12},
        },
    };

    saveFormRef = (form) => {
        this.form = form;
    };

    showModal = () => {
        this.setState({visible: true});

    };

    handleOk = e => {
        console.log(e);
        this.props.handleSubmit(e);
        this.setState({visible: false});

    };

    handleCancel = e => {
        console.log(e);
        this.setState({visible: false});
    };

    handleEdit = () => {
        const form = this.form;
        console.log(form)
        // form.validateFields((err, values) => {
        //     if (err) {
        //         return;
        //     }

            // dispatch action
            // this.props.handleSubmit(values);
            form.resetFields();
        // });
    };

    render() {
        return (
            <a>
                <Icon type="edit" onClick={this.showModal} theme="twoTone"/>
                <Modal
                    ref={this.saveFormRef}
                    visible={this.state.visible}
                    onOk={this.handleEdit}
                    onCancel={this.handleCancel}>
                    {/*<InputNumber min={0} max={10} defaultValue={this.props.time} step={0.01}/>*/}
                    {/*<Input size="large" defaultValue={this.props.text}/>*/}
                    <EditForm text={this.props.text}/>
                </Modal>
            </a>
        );
    }
}

export default EditModal;
