import React, {Component} from 'react';
import {Table} from 'antd';


class PresentedAnswers extends React.Component {

    state = {
        columns: [
            {
                dataIndex: 'answerText',
                key: 'answerText',
                render: text => <a href="javascript:;">{text}</a>,
            },
            {
                dataIndex: 'isCorrect',
                key: 'correctness',
            }],

        bordered: true,
        loading: false,
        pagination: true,
        size: 'medium',
        title: false,
        showHeader: false,
        footer: false,
        rowSelection: {},
        scroll: undefined

    };

    handleRowSelectionChange = (enable) => {
        this.setState({rowSelection: enable ? {} : undefined});
    };

    render() {
        const {["columns"]: columns, ...state} = this.state;
        console.log(this.props.data)
        state["rowSelection"] = {
            selectedRowKeys: [this.props.data.filter(answer => {if(answer.isCorrect) return answer.key})],
            onChange: (selectedRowKeys, selectedRows) => {
                console.log(`selectedRowKeys: ${selectedRowKeys}`, 'selectedRows: ', selectedRows);
            },
            getCheckboxProps: record => ({
                disabled: record.name === 'Disabled User', // Column configuration not to be checked
            }),
        };

        console.log(state);
        console.log(state["rowSelection"].selectedRowKeys);

        return (
            <div>
                <Table {...state} columns={columns} dataSource={this.props.data}/>
            </div>
        );
    }
}

export default PresentedAnswers;
