import React, {Component} from 'react';
import {Icon} from "antd";
import YTPlayer from "yt-player"

class VideoDetail extends Component {
    state = {
        video: null,
        player: null
    };

    onYouTubeIframeAPIReady() {
        return new YTPlayer('player', {
            height: '390',
            width: '640',
            videoId: 'M7lc1UVf-VE',
            // events: {
            //     'onReady': onPlayerReady,
            //     'onStateChange': onPlayerStateChange
            // }
        });
    }

    componentDidUpdate(prevProps) {
        if (this.props.video && ( prevProps.video !== this.props.video)) {
            this.setState({
                video: this.props.video,
                player: this.onYouTubeIframeAPIReady()
            });
        }
    }

    render() {
        const video = this.state.video;

        if (!video) {
            return (
                <div style={{
                    "width": "67.5%",
                    "background": "#999999",
                    "color": "#fff",
                    "height": "85vh",
                    "postion": "relative"
                }}>
                    <h1 style={{"top": "50%", "left": "28%", "position": "absolute"}}><Icon type={"youtube"}/></h1>
                </div>
            )
        }

        const videoId = video.id.videoId;
        const url = `https://www.youtube.com/embed/${ videoId }`;

        return (
            <div>
                <div className={"embed-responsive embed-responsive-16by9"}>
                    <div id="player"></div>
                    {/*<iframe title={video.snippet.title} className={"embed-responsive-item"} src={url} allowFullScreen/>*/}
                </div>
                <div>
                    <h2>
                        {video.snippet.title}
                    </h2>
                    <div>
                        {video.snippet.description}
                    </div>
                </div>
            </div>
        )
    }
}

export default VideoDetail;
