import React, {Component} from 'react';
import {Icon} from "antd";

class VideoDetail extends Component {
    state = {
        video: null
    };

    componentDidUpdate(prevProps) {
        if (this.props.video && ( prevProps.video !== this.props.video)) {
            this.setState({video: this.props.video})
        }
    }

    render() {
        const video = this.state.video;

        if (!video) {
            return (
                <div style={{
                    "width": "67.5%",
                    "background": "#999999",
                    "color": "#fff",
                    "height": "85vh",
                    "postion": "relative"
                }}>
                    <h1 style={{"top": "50%", "left": "28%", "position": "absolute"}}><Icon type={"youtube"}/></h1>
                </div>
            )
        }

        const videoId = video.id.videoId;
        const url = `https://www.youtube.com/embed/${ videoId }`;

        return (
            <div>
                <div className={"embed-responsive embed-responsive-16by9"}>
                    <video
                        id="vid1"
                        class="video-js vjs-default-skin"
                        controls
                        autoplay
                        width="640" height="264"
                        data-setup='{ "techOrder": ["youtube"], "sources": [{ "type": "video/youtube", "src": "https://www.youtube.com/watch?v=xjS6SftYQaQ"}] }'
                    >
                    </video>
                    {/*<video controls>*/}
                        {/*<iframe title={ video.snippet.title } className={"embed-responsive-item"} src={url} allowFullScreen />*/}
                        {/*/!*<source title={video.snippet.title} className={"embed-responsive-item"} src={url} type="video/mp4"/>*!/*/}
                    {/*</video>*/}
                </div>
                <div>
                    <h2>
                        {video.snippet.title}
                    </h2>
                    <div>
                        {video.snippet.description}
                    </div>
                </div>
            </div>
        )
    }
}

export default VideoDetail;
