import React, {Component} from 'react';
import {Provider} from 'react-redux';

import Search from './Components/Search'
import Video from './Components/Video'

import InputQuestion from './Components/InputQuestion'
import PresentedQuestions from './Components/PresentedQuestions'

import store from './store'

import './App.css';
import dotenv from 'dotenv';

dotenv.config();

class App extends Component {
    state = {videoIsSelected: null};

    isVideoSelected = video => {
        this.setState({videoIsSelected: !!video})
    }
    getPlayerTime = () => {
        console.log(this.videoRef)
        return this.videoRef.current.getTimeThatPassed();
    };

    render() {
        const videoIsSelected = !(this.state.videos === undefined || this.state.videos.length === 0);
        return (
            <Provider store={store}>
                <React.Fragment>
                    <Search isVideoSelected={this.isVideoSelected}/>
                    <Video/>
                    <div style={{
                        "display": "flex",
                        "justifyContent": "center"
                    }}>
                        {videoIsSelected && <InputQuestion getPlayerTime={this.getPlayerTime}/>}
                    </div>
                    <div style={{
                        "display": "flex",
                        "justifyContent": "center",
                        "paddingLeft": "20px",
                        "paddingRight": "20px"
                    }}>
                        <PresentedQuestions/>
                    </div>
                </React.Fragment>
            </Provider>
        );
    }
}

export default App;
