import React, {Component} from 'react';

import SearchBar from './SearchBar';
import YTSearch from 'youtube-api-search';
import {Icon, notification} from 'antd';


const API_KEY = "AIzaSyBIrrOQZ45NhLJX9UN8klnNrhyrlYX3QAk";

class Search extends Component {
    constructor(props) {
        super(props);
        this.state = {
            searched: false
        };
    }
    videoSearch(term) {
        YTSearch({key: API_KEY, term, part: "contentDetails"}, data => {
            try {
                if (data && data.data && data.data.error.message) {
                    throw ('error')
                }
                console.log(data)
                this.setState({searched:true});
                return {videos: data, selectedVideo: data[0]};
            } catch (err) {
                notification['error']({
                    message: "Daily Limit Exceeded",
                    description: "Youtube data API daily limit have exceeded. Quota will be recharged at 1:30pm IST. Wait till then.",
                })
            }
        });
    }

    handleInput = (value) => {
        if (value === '') {
            return;
        }
        const data = this.videoSearch(value);
        if (this.state.searched) {
            this.props.getData(data)
        }
    };

    render() {
        return (
            <React.Fragment>
                <div style={{
                    "display": "flex",
                    "flexDirection": "row",
                    "alignItems": "center",
                    "background": "#123456",
                    "paddingLeft": "30px",
                    "paddingRight": "30px",
                    "justifyContent": "space-between",
                }}>
                    <h1 style={{
                        "color": "#fff",
                        "paddingTop": "20px",
                    }}>Search <Icon type={"search"}/></h1>
                    <SearchBar onInput={this.handleInput}
                               style={{
                                   "width": "35%",
                                   "display": "inline-flex",
                               }}
                    />
                </div>
            </React.Fragment>
        )
    }
}

export default Search
