const search = require('../')
const test = require('tape')

const key = 'YOUR-API-KEY'

test('basic', function (t) {
  search('electron', {
    key: key
  }).then(results => {
    t.equals(results.length, 30, '30 results')
    t.end()
  }).catch(error => {
    t.notOk(error, 'no error')
    t.end()
  })
})
